#52 milli secs for large data setclass Solution {public:    int threeSumClosest(vector<int> &num, int target) {        int len = num.size();        assert(len >= 3);                vector<int> num2;        num2.assign(num.begin(), num.end());                sort(num2.begin(), num2.end());                int sum = num2[0] + num2[1] + num2[2];        int diff = abs(sum - target);        int ret = sum;                for(int i = 0; i < len - 2; i++){            int l = i + 1;            int r = len - 1;                        while(l < r){                sum = num2[i] + num2[l] + num2[r];                                if(sum == target) {                    return sum;                } else if (sum < target){                    l++;                } else {                    r--;                }                                if(abs(sum - target) < diff){       // more close, refresh diff and ret                    diff = abs(sum - target);                    ret = sum;                }            }        }        return ret;    }};