class Solution {public:    string addBinary(string a, string b) {        string str;        int i=a.length()-1,j=b.length()-1;        int flag=0,t;        while(i>=0&&j>=0){            t=flag+(a[i]-'0')+(b[j]-'0');            str+=t%2+'0';            flag=t/2;            --i,--j;        }        while(i>=0){            t=flag+(a[i]-'0');            str+=t%2+'0';            flag=t/2;            --i;        }        while(j>=0){            t=flag+(b[j]-'0');            str+=t%2+'0';            flag=t/2;            --j;        }        if(flag!=0)            str+='1';        i=0,j=str.length()-1;        char tmp;        while(i<j){            tmp=str[i];            str[i]=str[j];            str[j]=tmp;            ++i,--j;        }        return str;    }};