class Solution {public:    int result;    int maxSum(TreeNode *root){        if(root==NULL)            return 0;        int left=maxSum(root->left);        int right=maxSum(root->right);        int t,v;         t=max(root->val+right,root->val+left);        t=max(t,root->val);        v=t;        t=max(t,root->val+left+right);        if(t>result)            result=t;        return v;    }    int maxPathSum(TreeNode *root) {        if(root==NULL)            return 0;        result=root->val;        maxSum(root);        return result;    }};