#20 mili secs for large data setclass Solution {public:    int num;    vector<string> result;    void dfs(string str,int i,int j){        if(str.length()==num*2){            result.push_back(str);            return;        }        if(i<num)            dfs(str+'(',i+1,j);        if(j<num&&j<i)            dfs(str+')',i,j+1);    }    vector<string> generateParenthesis(int n) {        num=n;        result.clear();        dfs("",0,0);        return result;    }};