class Solution {public:    int lengthOfLastWord(const char *s) {        if(s==NULL||*s=='\0')            return 0;        string str="";        for(const char *ss=s;*ss!='\0';++ss)            str+=*ss;        int pos=str.find_last_of(' ');        int re=str.length()-1-pos;        if(re==0){            int re1=0,i=str.length()-2;            while(i>=0&&str[i]==' ')                --i;            for(;i>=0&&str[i]!=' ';i--)                ++re1;            return re1;        }        return re;    }};