#44 mili secs for large data setclass Solution {public:    int longestValidParentheses(string s) {        vector<int> sta;        int result=0,len=s.length();        if(len==0)            return 0;        sta.push_back(0);        for(int i=1;i<len;i++){            if(sta.size()!=0&&s[i]==')'&&s[sta[sta.size()-1]]=='('){                sta.pop_back();            }            else{                sta.push_back(i);            }        }        if(sta.size()==0)            return s.length();        for(int i=1;i<sta.size();i++){            result=max(result,sta[i]-sta[i-1]-1);        }        result=max(result,sta[0]-0);        result=max(result,len-1-sta[sta.size()-1]);        return result;    }};