#92 mili secs for large data setclass Solution {public:    ListNode *mergeKLists(vector<ListNode *> &lists) {        if(lists.size()==0)            return NULL;        int cnt=0;        ListNode *n1=lists[cnt];        ++cnt;        ListNode *t=new ListNode(0);        t->next=n1;        n1=t;        while(cnt<lists.size()){            ListNode *n2=lists[cnt];            ++cnt;            ListNode *n3=n1->next;            ListNode *pre=n1;            while(n2!=NULL&&n3!=NULL){                if(n2->val<n3->val){                    pre->next=n2;                    pre=pre->next;                    n2=n2->next;                }                else{                    pre->next=n3;                    pre=pre->next;                    n3=n3->next;                }            }            if(n2!=NULL)                pre->next=n2;            if(n3!=NULL)                pre->next=n3;        }        return n1->next;    }};