class Solution {public:    int minPathSum(vector<vector<int> > &grid) {        int r=grid.size();        int c=grid[0].size();        vector<vector<int> > result=grid;        result[0][0]=grid[0][0];        for(int i=1;i<c;i++)            result[0][i]=result[0][i-1]+grid[0][i];        for(int i=1;i<r;i++)            result[i][0]=result[i-1][0]+grid[i][0];        for(int i=1;i<r;i++){            for(int j=1;j<c;j++){                result[i][j]=min(result[i-1][j]+grid[i][j],result[i][j-1]+grid[i][j]);            }        }        return result[r-1][c-1];    }};