//28 mili secs for large data setclass Solution {public:    int m,*board;    vector<vector<string> > result;    vector<string> oneSolution;    bool illegal(int i, int j){        for(int k=0;k<i;k++){            if(board[k]==j||(board[k]+k)==(i+j)||(k-i)==(board[k]-j))                return false;        }        return true;    }    void dfs(int num){        if(num==m){            result.push_back(oneSolution);            return;        }        for(int j=0;j<m;j++){            if(illegal(num,j)){                board[num]=j;                oneSolution[num][j]='Q';                dfs(num+1);                oneSolution[num][j]='.';            }        }        return;    }    vector<vector<string> > solveNQueens(int n) {        m=n;        board=new int[n];        memset(board,0,sizeof(board));        result.clear();        oneSolution.clear();        string s="";        for(int i=0;i<n;i++)            s+='.';        for(int i=0;i<n;i++)            oneSolution.push_back(s);        dfs(0);        return result;    }};