#400 milli secs for large data setclass Solution {public:    bool isPalindrome(int x) {        if(x<0)            return false;        long long i=10,j=10;        while(x/j>0)            j*=10;        j/=10;        while(i<=j){            if((x%i-x%(i/10))/(i/10)!=x%(j*10)/j)                return false;            i*=10;            j/=10;        }        return true;    }};