class Solution {public:    string getPermutation(int n, int k) {        int p=1,m,t;        string result="";        int visit[10];        memset(visit,0,sizeof(visit));        for(int i=1;i<n;i++){            p*=i;        }        for(int i=n-1;i>=0;i--){            m=k/p,t=0;            if(k%p!=0)                m+=1;            for(int j=1;j<=n;j++){                if(visit[j]!=1)                    ++t;                if(t==m){                    result+='0'+j;                    visit[j]=1;                    break;                }            }            if(k>p){                k=k%p;                if(k==0)                    k=p;            }            if(i!=0)                p=p/i;        }        return result;    }};