class Solution {public:    bool searchMatrix(vector<vector<int> > &matrix, int target) {        int i=0,j=0;        if(matrix.size()==0)            return false;        if(matrix.size()==1){            for(j=0;j<matrix[0].size();j++){                if(matrix[0][j]==target)                    return true;            }            return false;           }        while(i<matrix.size()&&matrix[i][0]<=target)            ++i;        for(j=0;j<matrix[0].size();j++){            if(i-1>=0&&matrix[i-1][j]==target)                return true;        }        return false;    }};