class Solution {public:    vector<TreeNode *> generateTrees(int n) {                return getTrees(1, n);    }    vector<TreeNode*> getTrees(int start, int end) {        vector<TreeNode* > res;        if ( start > end ) {            res.push_back(NULL);            return res;        }        for (int i = start; i <= end; i++) {            vector<TreeNode*> left = getTrees(start, i-1);            vector<TreeNode*> right = getTrees(i+1, end);                        for (int l = 0; l < left.size(); l++) {                for (int r = 0; r < right.size() ;r++) {                    TreeNode* cur = new TreeNode(i);                    cur->left = left[l];                    cur->right = right[r];                    res.push_back(cur);                }            }        }    }};