#8 mili secs for large data set class Solution {public:    bool isValid(string s) {        if(s=="")            return true;        vector<char> r;        for(int i=0;i<s.length();i++){            if(s[i]=='['||s[i]=='{'||s[i]=='(')                r.push_back(s[i]);            else{                if(r.size()==0)                    return false;                char c=r[r.size()-1];                r.pop_back();                if(s[i]==']'&&c!='[')                    return false;                if(s[i]=='}'&&c!='{')                    return false;                if(s[i]==')'&&c!='(')                    return false;            }        }        if(r.size()!=0)            return false;        return true;    }};